const path ="./images"+"/"+props.item.category+"/"+props.item.name+".jpg";
    return(

<div onClick={()=>{navi('/product',{state:item})}} className=" max-h-72 max-w-xs flex flex-col bg-white justify-between px-2 py-2 bg-white-500 border-2 border-solid  transition-all duration-150 hover:scale-105 hover:shadow-lg hover:shadow-slate-600 rounded-lg " onDragStart={Drag}  >
<img className="  py-2 relative left-14 rounded-3xl max-w-36 max-h-40" src={path} ></img>

<h1 className="font-extrabold text-xl font-integral">&gt;{props.item.name}
</h1>
<div className="font-integral text-xs">{props.item.category}</div>
<div className=" flex py-2 justify-around  ">
<Button  name={t}/>
<Button fn={props.fn} name="Add to cart"/>
</div>
</div>
    
    )
}

export default Card;
