import './App.css';
import Nav from './components/Nav';
import Card from './components/Card';
import Submenu from './components/SubMenu';
import { useState,useEffect} from 'react';
import Banner from './components/Banner';
import ProdDesc from './components/productDes';
import { Route,Routes } from 'react-router-dom';
import Loader from './components/Loader';
import Login from './components/Login';
import SearchOp from './components/SearchOp';


function App() {
  var [dat,setD]=useState(null);
  async function fetc(){

  await fetch("http://localhost:8080/") 
     .then((res)=>res.json())
     .then((data)=>{setD(data);
  });
  }   

useEffect( ()=>{
 fetc();

},[]);



var [c,setC]=useState(0);
const [g,setG]=useState(false);
const [n,setNav]=useState(false);
const [s,setS]=useState(false);
const [d,setDO]=useState([null]);

function SearchB(get,set){
setDO(set);
setS(get);

}

function DataColl(val){
  setG(val);
}  
window.addEventListener('scroll',()=>{if(window.scrollY>=5){setNav(true);
  window.scrollTo({  behavior: 'smooth' });}


else{setNav(false)}});

function ClickCal(){
  c =c+1; 
 setC(c);
}
const f =69;
  return (

      <Routes>
        <Route path='/'  element={<div className="bg-ho bg-contain " ><div className="z-30 max-w-10 rounded-md bg-orange-400 fixed right-96 ">{c}</div>
      <Nav fn={DataColl} n={n} data={dat} setter={SearchB} />
  <SearchOp s={s} d={d}></SearchOp>
      <div className="font-integral bg-white text-2xl"> Latest in our stock</div>
      {g ? (dat?.map((d)=> (<Submenu data={d}></Submenu>))):(<></>)}
       <div className="grid grid-rows-12 grid-cols-5 gap-3 col-auto">

{dat?.map((item)=>( <Card fn={ClickCal} item={item}></Card>))} 

 </div> </div>}/>
<Route path='/product'  element={dat? <ProdDesc fn={DataColl} data={dat}></ProdDesc>:<></> } />
   <Route path='*'  element={<><div className="z-30 max-w-10 rounded-md bg-orange-400 fixed right-96 ">{c}</div>
      <Nav fn={DataColl} n={n} data={dat} setter={SearchB}/>

      <div className="font-integral text-2xl"> Latest in our stock</div>
      {/* {g ? (<Submenu/>):(<></>)} */}
       <div className="grid grid-rows-12 grid-cols-5 gap-3 col-auto">

{dat?.map((item)=>( <Card fn={ClickCal} item={item}></Card>))} 

 </div> </>}/>   
      </Routes>






  );
}

export default App;
