import { useLocation } from "react-router-dom";
import Nav from "./Nav";
function ProdDesc(props) {
  const location = useLocation();
  const item = location.state;
  const path = "./images" + "/" + item?.category + "/" + item.name + ".jpg";

  return (
    <>
      <Nav fn={props.fn}></Nav>
      <div className="w-3/4 flex flex-row">
        <div className="h-full w-1/2">
          <img className="w-3/4 " src={path}></img>
        </div>
        <div
          className="font-integral font-light
         text-2xl flex flex-col justify-around "
        >
          <h1>name:{item?.name}</h1>
          <h1>Category:{item?.category}</h1>
          <h1>price:{item?.price}</h1>
          <h2 className="text-xl max-w-xl">description:{item?.description}</h2>
          <div className="flex justify-between">
            <button className="rounded-md px-3 py-2 bg-black text-white text-lg font-integral">
              Buy now
            </button>
            <button className="rounded-md px-3 py-2 bg-black text-white text-lg font-integral">
              add to cart
            </button>
          </div>
        </div>
      </div>
    </>
  );
}

export default ProdDesc;
