import Nav from "./Nav";
const path =
  "./images" + "/" + props.item.category + "/" + props.item.name + ".jpg";
function Cart(props) {
  return (
    <>
      {/* <Nav></Nav> */}

      <div>
        <div className="flex justify-around ">
          <img src={path} className="w-1/5"></img>
          <div className="flex flex-col font-integral justify-around w-1/5">
            <h1>Name:{props.item.name}</h1>
            <h2 className="font-dmbold"> Quantity:5</h2>
            <span className="flex justify-around">
              <span className="bg-slate-300 rounded-md  px-2 my-1 ">-</span>
              <span>{5}</span>
              <span className="bg-slate-300 rounded-md  px-2 my-1 ">+</span>
            </span>
          </div>
          <div className="flex flex-col font-integral w-1/5 justify-around">
            <div>Rate{props.item.price}</div>
            <div>Quantity</div>
            <div>sub-total</div>
          </div>
        </div>
        <div></div>
      </div>
    </>
  );
}

export default Cart;
