import './App.css';
import Nav from './components/Nav';
import Card from './components/Card';
import Submenu from './components/SubMenu';
import { useState,useEffect} from 'react';
import Banner from './components/Banner';
import ProdDesc from './components/productDes';
import {BrowserRouter, Route,Routes } from 'react-router-dom';
import Loader from './components/Loader';


function App() {
  var [dat,setD]=useState(null);
  async function fetc(){

  await fetch("http://localhost:8080/") 
     .then((res)=>res.json())
     .then((data)=>{setD(data);
  });
  }   

useEffect( ()=>{
 fetc();

},[]);



var [c,setC]=useState(0);
const [g,setG]=useState(false);
const [n,setNav]=useState(false);
function DataColl(val){
  setG(val);
}  
window.addEventListener('scroll',()=>{if(window.scrollY>=5){setNav(true);
  window.scrollTo({  behavior: 'smooth' });}


else{setNav(false)}});

function ClickCal(){
  c =c+1; 
 setC(c);
}
  return (

      <Routes>
        <Route path='/'  element={<><div className="z-30 max-w-10 rounded-md bg-orange-400 fixed right-96 ">{c}</div>
      <Nav fn={DataColl} n={n} data={dat}/>
      <Banner></Banner>
      <div className="font-integral text-2xl"> Latest in our stock</div>
      {g ? (<Submenu/>):(<></>)}
       <div className="grid grid-rows-12 grid-cols-5 gap-3 col-auto">

{dat?.map((item)=>( <Card fn={ClickCal} item={item}></Card>))} 

 </div> </>}/>
<Route path='/product'  element={dat? <ProdDesc fn={DataColl} data={dat}></ProdDesc>:<></> } />
   <Route path='*'  element={<><div className="z-30 max-w-10 rounded-md bg-orange-400 fixed right-96 ">{c}</div>
      <Nav fn={DataColl} n={n} data={dat}/>
      <Banner></Banner>
      <div className="font-integral text-2xl"> Latest in our stock</div>
      {g ? (<Submenu/>):(<></>)}
       <div className="grid grid-rows-12 grid-cols-5 gap-3 col-auto">

{dat?.map((item)=>( <Card fn={ClickCal} item={item}></Card>))} 

 </div> </>}/>   
 <Route path='/l' element={<Loader></Loader>}></Route>
      </Routes>






  );
}

export default App;
