import { useNavigate } from "react-router-dom";
import Button from "./Button";
import { useState } from "react";
const t = "buy now";

function Card(props) {
  const navi = useNavigate();
  const item = props.item;

  const path =
    "./images" + "/" + props.item.category + "/" + props.item.name + ".jpg";
  return (
    <div
      onClick={() => {
        navi("/product", { state: item });
      }}
      className="bg-Card max-h-72 max-w-xs flex flex-col bg-white justify-between px-2 py-2 bg-white-500 border-2 border-solid transition-all duration-200 hover:scale-110 hover:shadow-lg hover:shadow-bermuda hover:text-white hover:bg-black rounded-lg "
    >
      <img
        className="  py-2 relative left-14 rounded-3xl max-w-36 max-h-40"
        src={path}
      ></img>
      <div className="flex justify-between ">
        <h1 className="font-extrabold text-md font-integral">
          &gt;{props.item.name}
        </h1>
        <h1 className="font-extrabold text-xl font-integral">
          â‚¹{props.item.price}
        </h1>
      </div>
      <div className="font-integral text-xs">{props.item.category}</div>
      <div className=" flex py-2 justify-around  ">
        <Button fn={props.c} price={props.pricea} name={t} item={props.item} />
        <Button
          fn={props.c}
          price={props.pricea}
          item={props.item}
          name="Add to cart"
        />
      </div>
    </div>
  );
}

export default Card;
