function ProdDesc(props){

const data= props.data;
// const path  ="./images"+"/"+data[2]?.category+"/"+data[2]?.name+".jpg";


    return(<>
    <Nav fn={props.fn}></Nav>
    <div className="flex flex-row h-full ">
    {/* <div className="h-full w-1/2  bg-black"><img className="w-full " src=""></img></div> */}
    <div className="font-integral text-3xl flex flex-col justify-around ">
        <h1>{data[2]?.name}</h1>
        <h1>data[2]?.category</h1>
        <h2 className="text-xl max-w-xl">data[2]?.description</h2>
        <div className="flex justify-between">
        <button  className="rounded-md px-3 py-2 bg-black text-white text-lg font-integral">Buy now</button>
        <button className="rounded-md px-3 py-2 bg-black text-white text-lg font-integral">add to cart</button>
        </div>
    </div>

</div></>
        
    )
}

export default ProdDesc;
