import "./App.css";
import Nav from "./components/Nav";
import Card from "./components/Card";
import Submenu from "./components/SubMenu";
import { useState, useEffect } from "react";
import Banner from "./components/Banner";
import ProdDesc from "./components/productDes";
import { Route, Routes } from "react-router-dom";
import Loader from "./components/Loader";
import Login from "./components/Login";
import SearchOp from "./components/SearchOp";
import Cart from "./components/Cart";
import { Button } from "react-scroll";
import CheckOut from "./checkout";
import Footer from "./components/Footer";

function App() {
  var [cart, setCart] = useState([]);
  var [dat, setD] = useState(null);
  async function fetc() {
    await fetch("http://localhost:8080/")
      .then((res) => res.json())
      .then((data) => {
        setD(data);
      });
  }

  useEffect(() => {
    fetc();
  }, []);
  var [t, setT] = useState(0);
  const [c, setC] = useState(false);
  const [g, setG] = useState(false);
  const [n, setNav] = useState(false);
  const [s, setS] = useState(false);
  const [d, setDO] = useState([null]);
  async function Totala(p) {
    await setT(() => {
      t = p + t;
      return t;
    });
    console.log(t);
  }
  async function Totals(p) {
    await setT(() => {
      t = t - p;
      return t;
    });
    console.log(t);
  }
  // useEffect(() => {
  //   Total();
  // }, [cart]);

  async function CartR(item) {
    if (cart.length <= 1) {
      setC(false);
    }
    await setCart(() => {
      cart = cart.filter((n) => item.name != n.name);

      return cart;
    });
  }
  function rD(arr) {
    return arr.filter((item, index) => arr.indexOf(item) === index);
  }
  function Carti(item) {
    if (cart.length >= 0) {
      setC(true);
    }
    setCart(() => {
      cart = [...cart, item];
      cart = rD(cart);

      return cart;
    });
  }

  function SearchB(get, set) {
    setDO(set);
    setS(get);
  }

  function DataColl(val) {
    setG(val);
  }
  window.addEventListener("scroll", () => {
    if (window.scrollY >= 5) {
      setNav(true);
      window.scrollTo({ behavior: "smooth" });
    } else {
      setNav(false);
    }
  });

  return (
    <>
      <Routes>
        {" "}
        <Nav fn={DataColl} n={n} data={dat} setter={SearchB}></Nav>
        <Route
          path="/"
          element={
            <div className="bg-black">
              <Banner></Banner>
              <div className="z-30 max-w-10 rounded-md bg-orange-400 fixed right-96 ">
                {cart.length}
              </div>
              <div className="absolute top-2 left-6 h-1/2  z-30">
                {" "}
                {s ? d?.map((da) => <SearchOp da={da}></SearchOp>) : <></>}
              </div>
              <SearchOp s={s} d={d}></SearchOp>

              {g ? dat?.map((d) => <Submenu data={d}></Submenu>) : <></>}

              <Footer></Footer>
            </div>
          }
        />
        <Route
          path="/cart"
          element={
            <>
              {cart?.map((item) => (
                <Cart
                  item={item}
                  pricea={Totala}
                  prices={Totals}
                  t={t}
                  fn={CartR}
                ></Cart>
              ))}

              {c ? (
                <button className="rounded-md px-3 py-1 bg-black text-white text-sm font-integral">
                  Checkout
                </button>
              ) : (
                <>
                  <img
                    src="./images/emptycart.svg"
                    className="w-1/3 relative top-5 left-96"
                  ></img>
                </>
              )}
            </>
          }
        >
          {" "}
        </Route>
        <Route path="/pay" element={<CheckOut></CheckOut>}></Route>
        <Route path="/login" element={<Login></Login>}></Route>
        <Route
          path="/product"
          element={dat ? <ProdDesc fn={DataColl} data={dat}></ProdDesc> : <></>}
        />
        <Route
          path="*"
          element={
            <>
              <div className="z-30 max-w-10 rounded-md bg-orange-400 fixed right-96 ">
                {cart.length}
              </div>
              <div className="font-integral text-2xl"> Latest in our stock</div>
              {/* {g ? (<Submenu/>):(<></>)} */}
              <div className="grid grid-rows-12 grid-cols-5 gap-3 col-auto">
                {dat?.map((item) => (
                  <Card
                    c={Carti}
                    pricea={Totala}
                    prices={Totals}
                    item={item}
                  ></Card>
                ))}
              </div>{" "}
            </>
          }
        />
      </Routes>
    </>
  );
}

export default App;
